import React, { useState, useEffect } from 'react';
import TickerTape from './components/tickerTape/TickerTape';
import './App.css';

function App() {
  const [stockData, setStockData] = useState([]);
  const [currentSector, setCurrentSector] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [darkMode, setDarkMode] = useState(false);

  // Track the count of displayed companies
  const [displayedCompanyCount, setDisplayedCompanyCount] = useState(0);

  const fetchData = () => {
    fetch('/api')
      .then((response) => response.json())
      .then((data) => {
        const { sector, data: sectorData } = data;

        // Update stockData to include sector data
        setStockData((prevData) => [
          ...prevData,
          { sector, data: sectorData },
        ]);

        setCurrentSector(sector);
        setIsLoading(false);
      });
  };

  console.log("Stock Data",stockData)

  // Function to reset the displayed company count
  const resetDisplayedCompanyCount = () => {
    setDisplayedCompanyCount(0);
  };

  useEffect(() => {
    fetchData();
  }, []); // Fetch data when the component mounts

  useEffect(() => {
    // Check if all companies in the current sector have been displayed
    if (displayedCompanyCount === stockData.length) {
      // If all companies have been displayed, initiate a new fetch
      resetDisplayedCompanyCount();
      fetchData();
    }
  }, [displayedCompanyCount, stockData]); // Watch for changes in displayedCompanyCount and stockData

  const bodyStyle = {
    backgroundColor: darkMode ? 'black' : 'white',
  };

  return (
    <div style={bodyStyle}>
      {isLoading ? (
        <div className="loader"></div>
      ) : (
        <div className="app-container">
          <hr style={{ borderColor: darkMode ? 'white' : 'black' }} />
          <div className="sectorName">{currentSector}</div>
          <TickerTape
            className="tickerTape"
            stockData={stockData}
            darkMode={darkMode}
            onCompanyDisplayed={() => setDisplayedCompanyCount(displayedCompanyCount + 1)} // Track displayed companies
          />

          <hr style={{ borderColor: darkMode ? 'white' : 'black' }} />
          <button
            className="DMbutton"
            onClick={() => {
              setDarkMode((prev) => !prev);
            }}
          >
            <i className="material-icons">
              {darkMode ? 'wb_sunny' : 'brightness_3'}
            </i>
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
