function TickerTape({ stockData, darkMode }) {
  const tickerRef = useRef(null);
  const [tickerPosition, setTickerPosition] = useState(0);
  const tickerWidth = 100; // Adjust this to the width of your ticker item
  const screenWidth = window.innerWidth;
  const itemsPerScreen = Math.floor(screenWidth / tickerWidth);
  const itemSpacing = 10; // Adjust the spacing between items
  const transitionThreshold = itemsPerScreen; // The number of items displayed before transitioning

  // Define currentSector using the stockData parameter
  const currentSector = stockData.length > 0 ? stockData[stockData.length - 1].sector : '';

  const currentSectorData = stockData.find((data) => data.sector === currentSector);
  const currentSectorCompanies = currentSectorData ? Object.keys(currentSectorData.data) : [];
  const [displayedCompanyCount, setDisplayedCompanyCount] = useState(0);

  useEffect(() => {
    const ticker = tickerRef.current;
    let animationFrameId;

    setTickerPosition(screenWidth);

    const updateTickerPosition = () => {
      setTickerPosition((prevPosition) => {
        const newPosition = prevPosition - 1;
        return newPosition <= -(tickerWidth + itemSpacing)
          ? screenWidth
          : newPosition;
      });

      animationFrameId = requestAnimationFrame(updateTickerPosition);
    };

    updateTickerPosition();

    return () => {
      cancelAnimationFrame(animationFrameId);
    };
  }, [screenWidth]);

  useEffect(() => {
    // Reset the displayed company count when the sector changes
    setDisplayedCompanyCount(0);
  }, [currentSector]);

  useEffect(() => {
    // Update the displayed company count when the ticker moves
    if (tickerPosition <= -(tickerWidth + itemSpacing)) {
      setDisplayedCompanyCount((prevCount) => prevCount + 1);

      if (displayedCompanyCount >= transitionThreshold) {
        // Transition to the next sector when the threshold is reached
        const sectors = stockData.map((dataItem) => dataItem.sector);
        const currentSectorIndex = sectors.indexOf(currentSector);
        const nextSectorIndex = (currentSectorIndex + 1) % sectors.length;
        const nextSector = sectors[nextSectorIndex];

        // Handle the sector transition as you like, for example, by setting the current sector state
        setCurrentSector(nextSector);
      }
    }
  }, [tickerPosition, currentSector, displayedCompanyCount, transitionThreshold]);

  return (
    <div className="ticker-container" style={{ backgroundColor: darkMode ? 'black' : 'white' }}>
      <div
        ref={tickerRef}
        className="ticker"
        style={{
          transform: `translateX(-${tickerPosition}px)`,
          backgroundColor: darkMode ? 'black' : 'white',
        }}
      >
        {stockData.map((dataItem) => {
          if (dataItem.sector === currentSector) {
            return Object.entries(dataItem.data).map(([companyName, companyData]) => {
              const value = (((companyData["c"] - companyData["o"]) / companyData["o"]) * 100).toFixed(2);

              return (
                <Company key={companyName} name={companyName} value={value} darkMode={darkMode} />
              );
            });
          }
          return null;
        })}
      </div>
    </div>
  );
}
