import React, { useState, useEffect, useRef } from 'react';
import Company from '../company/Company';
import './TickerTape.css';

function TickerTape({ stockDataKeys, stockData , darkMode}) {
  const tickerRef = useRef(null);
  const [tickerPosition, setTickerPosition] = useState(0);
  const tickerWidth = 100; // Adjust this to the width of your ticker item
  const screenWidth = window.innerWidth; // Get the screen width

  useEffect(() => {
    const ticker = tickerRef.current;
    let animationFrameId;

    // Set the initial position to start from the right
    setTickerPosition(screenWidth);

    const updateTickerPosition = () => {
      setTickerPosition((prevPosition) => {
        // Move the ticker tape from right to left
        const newPosition = prevPosition - 1; // Adjust the speed by changing the value
        return newPosition <= -tickerWidth ? screenWidth : newPosition;
      });

      animationFrameId = requestAnimationFrame(updateTickerPosition);
    };

    updateTickerPosition();

    // Cleanup when the component unmounts
    return () => {
      cancelAnimationFrame(animationFrameId);
    };
  }, [stockDataKeys, screenWidth]);

  const hardcodedCompanies = [
    {
      name: 'FB',
      value: '-0.03',
    },
    {
      name: 'TSLA',
      value: '0.25',
    },
    {
      name: 'NVDA',
      value: '-0.14',
    },
    {
      name: 'AMD',
      value: '-0.27',
    },
    {
      name: 'ORCL',
      value: '0.19',
    },
  ];

  return (
    <div className="ticker-container" style={{backgroundColor:darkMode?"black":"white"}}>
      <div
        ref={tickerRef}
        className="ticker"
        style={{ transform: `translateX(-${tickerPosition}px)` , backgroundColor:darkMode?"black":"white"}} // TranslateX with a negative value to slide from right to left
      >
        {stockDataKeys.map((key) => {
          const dataItem = stockData[key];
          const value = (((dataItem["4. close"] - dataItem["1. open"]) / dataItem["1. open"]) * 100).toFixed(2);

          return <Company key={key} name={key} value={value} darkMode={darkMode} />;
        })}

        {hardcodedCompanies.map((company) => (
          <Company key={company.name} name={company.name} value={company.value} darkMode={darkMode} />
        ))}
      </div>
    </div>
  );
}

export default TickerTape;
