import React, { useState, useEffect } from 'react';
import TickerTape from './components/tickerTape/TickerTape';
import './App.css';

function App() {
  const [stockData, setStockData] = useState([]);
  const [stockDataKeys, setStockDataKeys] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [darkMode,setDarkMode] = useState(false)



  useEffect(() => {
    console.log("in use effect")
    fetch('/api')
      .then(response => response.json())
      .then(data => {
        let dkey = Object.keys(data);
        setStockData(data);
        setStockDataKeys(dkey);
        setIsLoading(false);
        console.log(stockData)
        console.log(stockDataKeys)
      });
  }, []);

    if (isLoading) {
      return <div>Loading...</div>; // Show loading message
    } else if (stockDataKeys.length > 0) {

      return (
        <div className="app-container">
          <TickerTape stockDataKeys={stockDataKeys} stockData={stockData} darkMode={darkMode}/>
          <button onClick={()=>{setDarkMode(prev => !prev); console.log(darkMode)}}>Dark Mode</button>
      </div>
      );
    } else {
      return <div>No data available.</div>;
    }


}

export default App;
