import React, { useState, useEffect } from 'react';
import TickerTape from './components/tickerTape/TickerTape';
import './App.css';

function App() {
  const [sectorData, setSectorData] = useState({}); // Initialize as an empty object
  const [isLoading, setIsLoading] = useState(true);
  const [darkMode, setDarkMode] = useState(false);

  const fetchData = () => {
    fetch('/api')
      .then((response) => response.json())
      .then((data) => {
        const { sector, data: sectorData } = data;
        // Update the data for the sector without overwriting other sectors
        setSectorData((prevSectorData) => ({
          ...prevSectorData,
          [sector]: sectorData,
        }));
        setIsLoading(false);
      });
  };

  useEffect(() => {
    fetchData();

    const interval = setInterval(fetchData, 60000);

    return () => clearInterval(interval);
  }, []);

  const bodyStyle = {
    backgroundColor: darkMode ? 'black' : 'white',
  };

  return (
    <div style={bodyStyle}>
      {isLoading ? (
        <div className="loader"></div>
      ) : (
        <div className="app-container">
          <TickerTape
            className="tickerTape"
            stockData={sectorData} // Pass data for all sectors
            darkMode={darkMode}
          />
          <hr style={{ borderColor: darkMode ? 'white' : 'black' }} />
          <button
            className="DMbutton"
            onClick={() => {
              setDarkMode((prev) => !prev);
            }}
          >
            <i className="material-icons">
              {darkMode ? 'wb_sunny' : 'brightness_3'}
            </i>
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
