import React, { useState, useEffect, useRef } from 'react';
import Company from '../company/Company';
import './TickerTape.css';

function TickerTape({ stockData, darkMode }) {
  const tickerRef = useRef(null);
  const [tickerPosition, setTickerPosition] = useState(0);
  const tickerWidth = 100; // Adjust this to the width of your ticker item
  const screenWidth = window.innerWidth;
  const itemsPerScreen = Math.floor(screenWidth / tickerWidth);
  const itemSpacing = 10; // Adjust the spacing between items

  // Define currentSector using the stockData parameter
  const currentSector = stockData.length > 0 ? stockData[stockData.length - 1].sector : '';

  // Use an effect to update the ticker position when the data changes
  useEffect(() => {
    // Calculate the total width of all items in the ticker
    const totalTickerWidth = itemsPerScreen * (tickerWidth + itemSpacing);

    // Calculate the remaining distance based on the number of items and the ticker's position
    const remainingDistance = (stockData.length * totalTickerWidth) - tickerPosition;

    // Calculate the duration of the animation to make the ticker slide smoothly
    const animationDuration = (remainingDistance / screenWidth) * 60 * 1000; // 60 frames per second

    // Update the ticker position and animation duration
    setTickerPosition(remainingDistance);
    tickerRef.current.style.animationDuration = animationDuration + 'ms';

    // Add the 'animate' class to start the animation
    tickerRef.current.classList.add('animate');

    // Remove the 'animate' class when the animation ends
    const onAnimationEnd = () => {
      tickerRef.current.classList.remove('animate');
      tickerRef.current.removeEventListener('animationend', onAnimationEnd);
    };
    tickerRef.current.addEventListener('animationend', onAnimationEnd);
  }, [stockData, itemsPerScreen, tickerWidth, itemSpacing]);

  return (
    <div className="ticker-container" style={{ backgroundColor: darkMode ? 'black' : 'white' }}>
      <div
        ref={tickerRef}
        className="ticker"
        style={{
          transform: `translateX(-${tickerPosition}px)`,
          backgroundColor: darkMode ? 'black' : 'white',
        }}
      >
        {stockData.map((dataItem) => {
          if (dataItem.sector === currentSector) {
            return Object.entries(dataItem.data).map(([companyName, companyData]) => {
              const value = (((companyData["c"] - companyData["o"]) / companyData["o"]) * 100).toFixed(2);

              return (
                <Company key={companyName} name={companyName} value={value} darkMode={darkMode} />
              );
            });
          }
          return null;
        })}
      </div>
    </div>
  );
}

export default TickerTape;
