import React, { useState, useEffect } from 'react';
import TickerTape from './components/tickerTape/TickerTape';
import './App.css';

function App() {
  const [stockData, setStockData] = useState([]);
  const [stockDataKeys, setStockDataKeys] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [darkMode,setDarkMode] = useState(false)
  const [selectedSector, setSelectedSector] = useState('');



  useEffect(() => {
    console.log("in use effect")
    fetch(`/api`)
      .then(response => response.json())
      .then(data => {
        let dkey = Object.keys(data);
        setStockData(data);
        setStockDataKeys(dkey);
        setIsLoading(false);
        console.log(stockData)
        console.log(stockDataKeys)
      });
  }, []);


  function handleButtonClick(sectorName) {
    console.log("Button clicked with sector:", sectorName);

    // Fetch data for the selected sector
    fetch(`/api?sector=${sectorName}`)
      .then(response => response.json())
      .then(data => {
        let dkey = Object.keys(data);
        setStockData(data);
        setStockDataKeys(dkey);
        setIsLoading(false);
        console.log(stockData)
        console.log(stockDataKeys)
      });
  }

  function handleDropdownChange(event) {
    const selectedOption = event.target.value;
    setSelectedSector(selectedOption); // Update the selected sector when an option is selected
    handleButtonClick(selectedOption); // Trigger the function with the selected sector
  }




    if (isLoading) {
      return <div>Loading...</div>; // Show loading message
    } else if (stockDataKeys.length > 0) {

      /* 'Technology Services', 'Consumer Non-Durables', 'Finance', 'Health Technology', 'Electronic Technology', 'Retail Trade', 'Distribution Services', 'Consumer Durables', 'Producer Manufacturing', 'Commercial Services', 'Industrial Services', 'Utilities', 'Process Industries', 'Non-Energy Minerals', 'Transportation', 'Health Services', 'Energy Minerals', 'Communications' */
      return (
        <div className="app-container">
          <div>
            <select onChange={handleDropdownChange} value={selectedSector}>
              <option value="">Select a sector</option>
              <option value="Consumer Services">Consumer Services</option>
              <option value="Commercial Services">Commercial Services</option>
              {/* Add more sectors here */}
            </select>
          </div>

          <TickerTape className="tickerTape" stockDataKeys={stockDataKeys} stockData={stockData} darkMode={darkMode}/>
          <button className="DMbutton" onClick={()=>{setDarkMode(prev => !prev); console.log(darkMode)}}>
          <i className="material-icons">
            {darkMode ? 'wb_sunny' : 'brightness_3'} {/* Use 'wb_sunny' for sun icon and 'brightness_3' for moon icon */}
          </i>
          </button>

      </div>
      );
    } else {
      return <div>No data available.</div>;
    }


}

export default App;
