import React, { useState, useEffect } from 'react';
import TickerTape from './components/tickerTape/TickerTape';
import './App.css';

function App() {
  const [stockData, setStockData] = useState([]);
  const [stockDataKeys, setStockDataKeys] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [darkMode,setDarkMode] = useState(false)

  function handleButtonClick(sectorName) {
    fetch(`/api/get_stock_data?sector=${sectorName}`)
      .then((response) => response.json())
      .then((data) => {
        // Handle the response data from the Flask endpoint here
        console.log(data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }



  useEffect(() => {
    console.log("in use effect")
    fetch('/api')
      .then(response => response.json())
      .then(data => {
        let dkey = Object.keys(data);
        setStockData(data);
        setStockDataKeys(dkey);
        setIsLoading(false);
        console.log(stockData)
        console.log(stockDataKeys)
      });
  }, []);

    if (isLoading) {
      return <div>Loading...</div>; // Show loading message
    } else if (stockDataKeys.length > 0) {

      return (
        <div className="app-container">
          <div>
            <button onClick={() => handleButtonClick('Consumer Services')}>Consumer Services</button>
            <button onClick={() => handleButtonClick('Commerical Services')}>Commerical Services</button>
          </div>

          <TickerTape className="tickerTape" stockDataKeys={stockDataKeys} stockData={stockData} darkMode={darkMode}/>
          <button className="DMbutton" onClick={()=>{setDarkMode(prev => !prev); console.log(darkMode)}}>
          <i className="material-icons">
            {darkMode ? 'wb_sunny' : 'brightness_3'} {/* Use 'wb_sunny' for sun icon and 'brightness_3' for moon icon */}
          </i>
          </button>

      </div>
      );
    } else {
      return <div>No data available.</div>;
    }


}

export default App;
