import React, { useState, useEffect } from 'react';
import TickerTape from './components/tickerTape/TickerTape';
import './App.css';
import sectors from './sectorData';

function App() {
  const [stockData, setStockData] = useState([]);
  const [stockDataKeys, setStockDataKeys] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [darkMode,setDarkMode] = useState(false)
  const [selectedSector, setSelectedSector] = useState('');
  const [isFetching, setIsFetching] = useState(false); 



  useEffect(() => {
    console.log("in use effect")
    fetch(`/api`)
      .then(response => response.json())
      .then(data => {
        let dkey = Object.keys(data);
        setStockData(data);
        setStockDataKeys(dkey);
        setIsLoading(false);
        console.log(stockData)
        console.log(stockDataKeys)
      });
  }, []);


  function handleButtonClick(sectorName) {
    console.log("Button clicked with sector:", sectorName);

    setIsFetching(true);

    // Fetch data for the selected sector
    fetch(`/api?sector=${sectorName}`)
      .then(response => response.json())
      .then(data => {
        let dkey = Object.keys(data);
        setStockData(data);
        setStockDataKeys(dkey);
        setIsLoading(false);
        setIsFetching(false);
        console.log(stockData)
        console.log(stockDataKeys)
      });
  }

  function handleDropdownChange(event) {
    const selectedOption = event.target.value;
    setSelectedSector(selectedOption); // Update the selected sector when an option is selected
    handleButtonClick(selectedOption); // Trigger the function with the selected sector
  }

  const bodyStyle = {
    backgroundColor: darkMode ? 'black' : 'white',
    color: darkMode ? 'white' : 'black',
  };



  return (
    <div style={bodyStyle}>
      {isLoading ? (
        <div className="loader"></div>
      ) : (
        <div className="app-container">
          <div className='Dropdown'>
            <select style={{ backgroundColor: darkMode ? "black" : "white", color: darkMode ? "white" : "black", borderColor: darkMode ? "white" : "black" }} onChange={handleDropdownChange} value={selectedSector}>
              <option value="">Top 5</option>
              {sectors.map(sector => (
                <option key={sector} value={sector}>{sector}</option>
              ))}
            </select>
          </div>

          <hr style={{ borderColor: darkMode ? "white" : "black" }} />
          {isFetching ? (
            <div className="loader"></div>
          ) : (
            <>
              <TickerTape
                className="tickerTape"
                stockDataKeys={stockDataKeys}
                stockData={stockData}
                darkMode={darkMode}
              />
            </>
          )}
          <hr style={{ borderColor: darkMode ? "white" : "black" }} />
          <button className="DMbutton" onClick={() => { setDarkMode(prev => !prev); console.log(darkMode) }}>
            <i className="material-icons">
              {darkMode ? 'wb_sunny' : 'brightness_3'} {/* Use 'wb_sunny' for sun icon and 'brightness_3' for moon icon */}
            </i>
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
