import React, { useState, useEffect } from 'react';
import TickerTape from './components/tickerTape/TickerTape';
import './App.css';
import sectors from './sectorData';

function App() {
  const [stockData, setStockData] = useState([]);
  const [stockDataKeys, setStockDataKeys] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [darkMode, setDarkMode] = useState(false);

  // Define a function to fetch data
  const fetchData = () => {
    fetch(`/api`)
      .then((response) => response.json())
      .then((data) => {
        let dkey = Object.keys(data);
        setStockData(data);
        setStockDataKeys(dkey);
        setIsLoading(false);
      });
  };

  // Use a timer to fetch data every 60 seconds
  useEffect(() => {
    // Initial data fetch
    fetchData();

    const interval = setInterval(fetchData, 60000); // Fetch data every minute

    return () => clearInterval(interval); // Clean up interval
  }, []);

  const bodyStyle = {
    backgroundColor: darkMode ? 'black' : 'white',
  };

  return (
    <div style={bodyStyle}>
      {isLoading ? (
        <div className="loader"></div>
      ) : (
        <div className="app-container">
          <hr style={{ borderColor: darkMode ? 'white' : 'black' }} />
          <TickerTape
            className="tickerTape"
            stockDataKeys={stockDataKeys}
            stockData={stockData}
            darkMode={darkMode}
          />
          <hr style={{ borderColor: darkMode ? 'white' : 'black' }} />
          <button
            className="DMbutton"
            onClick={() => {
              setDarkMode((prev) => !prev);
            }}
          >
            <i className="material-icons">
              {darkMode ? 'wb_sunny' : 'brightness_3'}{' '}
              {/* Use 'wb_sunny' for sun icon and 'brightness_3' for moon icon */}
            </i>
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
