import React, { useState, useEffect } from 'react';
import Company from './components/company/Company';

function App() {
  const [stockData, setStockData] = useState([]);
  const [stockDataKeys, setStockDataKeys] = useState([]);
  const [isLoading, setIsLoading] = useState(true);



  useEffect(() => {
    console.log("in use effect")
    fetch('/api')
      .then(response => response.json())
      .then(data => {
        let dkey = Object.keys(data);
        setStockData(data);
        setStockDataKeys(dkey);
        setIsLoading(false);
        console.log(stockData)
        console.log(stockDataKeys)
      });
  }, []);

    if (isLoading) {
      return <div>Loading...</div>; // Show loading message
    } else if (stockDataKeys.length > 0) {
      // const percentage =
      //   (((stockData[lastElement]["4. close"] - stockData[lastElement]["1. open"]) / stockData[lastElement]["1. open"])*100).toFixed(2);

      return (
        <div>
        {stockDataKeys.map((key) => {
          const dataItem = stockData[key];
          const value = (((dataItem["4. close"] - dataItem["1. open"]) / dataItem["1. open"])*100).toFixed(2)
          // Check if dataItem exists and has the expected properties
          if (dataItem && dataItem["1. open"] && dataItem["4. close"]) {
            return (
              <div key={key}>
                <Company name={key} value={value}/>
                <hr />
              </div>
            );
          } else {
            return null; // Skip rendering if data is incomplete
          }
        })}
      </div>
      );
    } else {
      return <div>No data available.</div>;
    }


}

export default App;
